mapper.update:
UPDATE quartz_job
bean_name = #{beanName}, method_name = #{methodName}, params = #{params}, cron_expression = #{cronExpression}, status = #{status}, remark = #{remark}, gmt_modified = NOW()
WHERE job_id = #{jobId}

UPDATE quartz_job 
SET status = #{status} 
WHERE
	job_id in

	#{jobId}
